import React from "react";

function ProgressBar({
  isFocus,
  focusCount,
  focusDuration,
  breakCount,
  breakDuration,
  barWidth,
  setBarWidth,
  ariaValue,
  setAriaValue,
}) {
  const timeElapsedA = focusDuration * 60 - focusCount;
  const timeElapsedB = breakDuration * 60 - breakCount;

  let percentA = (timeElapsedA / (focusDuration * 60)) * 100;
  let percentB = (timeElapsedB / (breakDuration * 60)) * 100;

  //focusBar
  //const ariaValue = percentA.toFixed(2);
  setBarWidth(`${percentA}%`);
  setAriaValue(percentA.toFixed(2));

  console.log(barWidth);

  return (
    <div className="row mb-2">
      <div className="col">
        <div className="progress d-none">
          <div
            className="progress-bar"
            role="progressbar"
            aria-valuemin="0"
            aria-valuemax="100"
            aria-valuenow={`${ariaValue}`} // TODO: Increase aria-valuenow as elapsed time increases
            style={{ width: `${barWidth}%` }} // TODO: Increase width % as elapsed time increases
          />
        </div>
      </div>
    </div>
  );

