import React from "react";
import {setFocusCount, set} from "./Pomodoro";

const onFocusIncrease = () => {
  console.log("increasing focus time...");
  if (focusDuration < 60) {
    setFocusCount((prevCount) => prevCount + 5);
    setFocusDuration((prevCount) => prevCount + 5);
  } else {
    console.log("max duration reached");
  }
  console.log("focusDuration:", focusDuration, "focusCount:", focusCount);
};

const onFocusDecrease = () => {
  console.log("decreasing focus time...");
  if (focusDuration > 1) {
    setFocusCount((prevCount) => prevCount - 1);
    setFocusDuration((prevCount) => prevCount - 1);
  } else {
    console.log("min duration reached");
  }
  console.log("focusCount:", focusCount, "focusDuration:", focusDuration);
};

function FocusButtons() {
  return (
    <div className="input-group-append">
      {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
      <button
        type="button"
        className="btn btn-secondary"
        id="timerButton"
        data-testid="decrease-focus"
        onClick={onFocusDecrease}
      >
        <span className="oi oi-minus" />
      </button>
      {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
      <button
        type="button"
        className="btn btn-secondary"
        id="timerButton"
        data-testid="increase-focus"
        onClick={onFocusIncrease}
      >
        <span className="oi oi-plus" />
      </button>
    </div>
  );
}

export default FocusButtons;
