import React from "react";

function ProgressBar({
  isFocus,
  focusCount,
  focusDuration,
  breakCount,
  breakDuration,
}) {
  const timeElapsedA = focusDuration * 60 - focusCount;
  const timeElapsedB = breakDuration * 60 - breakCount;

  let percentA = (timeElapsedA / (focusDuration * 60)) * 100;
  let percentB = (timeElapsedB / (breakDuration * 60)) * 100;

  const bar = document.querySelector(".progress-bar");

  const barA = () => {
    bar.style.width = `${percentA}%`;
    bar.setAttribute("aria-valuenow", percentA.toFixed(2));
  };
  const barB = () => {
    bar.style.width = `${percentB}%`;
    bar.setAttribute("aria-valuenow", percentB.toFixed(2));
  };
  //isFocus ? barA : barB
  //let width = "";
  //let ariaValue = "";

  if (isFocus) {
    //barA
    const width = `${percentA}%`;
    const ariaValue = percentA.toFixed(2);
  } else {
    //barB
    const width = `${percentB}%`;
    ariaValue = percentB.toFixed(2);
  }

  return (
    <div className="row mb-2">
      <div className="col">
        <div className="progress d-none">
          <div
            className="progress-bar"
            role="progressbar"
            aria-valuemin="0"
            aria-valuemax="100"
            aria-valuenow={`${ariaValue}`} // TODO: Increase aria-valuenow as elapsed time increases
            style={{ width: `${width}%` }} // TODO: Increase width % as elapsed time increases
          />
        </div>
      </div>
    </div>
  );
}

export default ProgressBar;
