import React, { useState } from "react";
import classNames from "../utils/class-names";
import useInterval from "../utils/useInterval";
import { minutesToDuration } from "../utils/duration";

function Pomodoro() {
  // Timer state
  const [isTimerRunning, setIsTimerRunning] = useState(false);

  function playPause() {
    //sets timer to on or off
    setIsTimerRunning((prevState) => !prevState);
    //ON PAUSE
    if (isTimerRunning === true) {
      onPause();
    } else if (isTimerRunning === false) {
      // ON PLAY
      onPlay();
      // on play handle progress bar
    }
    //handle progress bar
    handleProgressBar();
  }

  const onPlay = () => {
    console.log("Play");
    console.log(
      `current focusCount:${focusCount} focusDuration:${focusDuration} breakCount:${breakCount} breakDuration:${breakDuration}`
    );
    //enable stop button
    const stopBtn = document.querySelector("#stopBtn");
    stopBtn.classList.remove("disabled");
    //disable timer buttons
    const timerBtns = document.querySelectorAll("#timerButton");
    ///iterate nodeList and add disable buttons
    for (let btn of timerBtns) {
      //console.log(btn);
      btn.setAttribute("disabled", true);
    }
    //showing session info after play click
    showSessionInfo();
    const progressBar = document.querySelector(".progress");
    progressBar.classList.remove("d-none");
    progressBar.classList.add("flex");

    // hide paused notification div
    const paused = document.querySelector("#paused");
    paused.classList.add("d-none");
    paused.classList.remove("d-block");
  };

  const onPause = () => {
    console.log("Paused");
    console.log(
      `Remaining: focusCount:${focusCount} focusDuration:${focusDuration} breakCount:${breakCount} breakDuration:${breakDuration}`
    );
    //show paused notification div
    const paused = document.querySelector("#paused");
    paused.classList.add("d-block");
    paused.classList.remove("d-none");
  };

  function disableStopButton() {
    const stopBtn = document.querySelector("#stopBtn");
    stopBtn.classList.add("disabled");
    stopBtn.getAttribute("disabled", true);
  }

  function hideSessionInfo() {
    const sessionInfo = document.querySelector("#sessionInfo");
    sessionInfo.classList.add("d-none");
    sessionInfo.classList.remove("d-block");
  }

  function showSessionInfo() {
    const sessionInfo = document.querySelector("#sessionInfo");
    sessionInfo.classList.remove("d-none");
    sessionInfo.classList.add("d-block");
  }

  function hideProgressBar() {
    const progressBar = document.querySelector(".progress");
    progressBar.classList.add("d-none");
    progressBar.classList.remove("flex");
  }

  //stop current session
  const onStop = () => {
    console.log("Stop");

    setIsTimerRunning(false);
    setDisplay("Focusing for");
    setFocusCount(25);
    setFocusDuration(25);
    setBreakCount(5);
    setBreakDuration(5);

    // disable stop button after click
    disableStopButton();
    //hiding session info after stop click
    hideSessionInfo();
    //hide progress bar
    hideProgressBar();

    //re-enable timer buttons
    const timerBtns = document.querySelectorAll("#timerButton");
    ///iterate nodeList and remove disable
    for (let btn of timerBtns) {
      //console.log(btn);
      btn.removeAttribute("disabled");
    }
  };

  const runFocusSession = () => {
    if (focusCount > 0) {
      console.log("FocusCount:", focusCount, "FocusDuration:", focusDuration);
      setFocusCount(focusCount - 1);
      setTimeRemaining(focusCount);
      setTimeElapsed((prevCount) => prevCount + 1);
      //console.log("timeElapsed:", timeElapsed);
      //ADD aria handler now
    } else {
      console.log("Focus Session Complete");
      //reset focus count
      setFocusCount(focusDuration);
      setTimeRemaining(0);
      handleDisplay();
      runBreakSession();
    }
  };

  const runBreakSession = () => {
    if (breakCount > 0) {
      console.log("BreakCount:", breakCount, "BreakDuration:", breakDuration);
      setBreakCount(breakCount - 1);
      setTimeRemaining(breakCount);
      setTimeElapsed((prevCount) => prevCount + 1);
      console.log("break count:", breakCount);
    } else {
      console.log("Break Session Complete");
      //reset break count
      setBreakCount(breakDuration);
    }
  };

  //TIMER
  useInterval(
    () => {
      // ToDo: Implement what should happen when the timer is running
      runFocusSession();
    },
    isTimerRunning ? 1000 : null
  );

  //useInterval(() => {}, isTimerRunning ? 1000 : null);

  //sets display
  const [display, setDisplay] = useState("Focusing for");
  const handleDisplay = () => {
    setDisplay("On Break for");
  };

  ///////////////
  ////STATES/////
  ///////////////
  const [focusCount, setFocusCount] = useState(25);
  //set focus duration label
  const [focusDuration, setFocusDuration] = useState(focusCount);

  //set break
  const [breakCount, setBreakCount] = useState(5);
  const [breakDuration, setBreakDuration] = useState(breakCount);

  //time remaining
  const [timeElapsed, setTimeElapsed] = useState(0);
  const [timeRemaining, setTimeRemaining] = useState(focusCount - timeElapsed);

  /////////////
  //// END ////
  /////////////

  //mg:
  //focus time
  const handleFocusIncrease = () => {
    console.log("increasing focus time...");
    if (focusDuration < 60) {
      setFocusCount((prevCount) => prevCount + 5);
      setFocusDuration((prevCount) => prevCount + 5);
    } else {
      console.log("max duration reached");
    }
    console.log("focusDuration:", focusDuration, "focusCount:", focusCount);
  };

  const handleFocusDecrease = () => {
    console.log("decreasing focus time...");
    if (focusDuration > 5) {
      setFocusCount((prevCount) => prevCount - 5);
      setFocusDuration((prevCount) => prevCount - 5);
    } else {
      console.log("min duration reached");
    }
    console.log("focusCount:", focusCount, "focusDuration:", focusDuration);
  };

  //break time
  const handleBreakIncrease = () => {
    console.log("increasing break...");
    if (breakDuration < 15) {
      //change count
      setBreakDuration(breakDuration + 1);
    } else {
      console.log("max duration reached");
    }
    console.log("count:", "n/a", "breakDuration:", breakDuration);
  };

  const handleBreakDecrease = () => {
    console.log("decreasing break...");
    if (breakDuration > 1) {
      //change count
      setBreakDuration(breakDuration - 1);
    } else {
      console.log("min duration reached");
    }
    console.log("count:", "n/a", "breakDuration:", breakDuration);
  };

  //handle progress bar
  const handleProgressBar = () => {
    const percent = (timeElapsed / focusDuration) * 100;
    const progressBar = document.querySelector(".progress-bar");
    progressBar.setAttribute("aria-valuenow", `${percent}%`);
    progressBar.style.width = `${percent}%`;
  };

  return (
    <div className="pomodoro">
      <div className="row">
        <div className="col">
          <div className="input-group input-group-lg mb-2">
            <span className="input-group-text" data-testid="duration-focus">
              {/* TODO: Update this text to display the current focus session duration */}
              Focus Duration: {minutesToDuration(focusDuration)}
            </span>
            <div className="input-group-append">
              {/* TODO: Implement decreasing focus duration and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                id="timerButton"
                data-testid="decrease-focus"
                onClick={handleFocusDecrease}
              >
                <span className="oi oi-minus" />
              </button>
              {/* TODO: Implement increasing focus duration  and disable during a focus or break session */}
              <button
                type="button"
                className="btn btn-secondary"
                id="timerButton"
                data-testid="increase-focus"
                onClick={handleFocusIncrease}
              >
                <span className="oi oi-plus" />
              </button>
            </div>
          </div>
        </div>
        <div className="col">
          <div className="float-right">
            <div className="input-group input-group-lg mb-2">
              <span className="input-group-text" data-testid="duration-break">
                {/* TODO: Update this text to display the current break session duration */}
                Break Duration: {minutesToDuration(breakDuration)}
              </span>
              <div className="input-group-append">
                {/* TODO: Implement decreasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  id="timerButton"
                  data-testid="decrease-break"
                  onClick={handleBreakDecrease}
                >
                  <span className="oi oi-minus" />
                </button>
                {/* TODO: Implement increasing break duration and disable during a focus or break session*/}
                <button
                  type="button"
                  className="btn btn-secondary"
                  id="timerButton"
                  data-testid="increase-break"
                  onClick={handleBreakIncrease}
                >
                  <span className="oi oi-plus" />
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="row">
        <div className="col">
          <div
            className="btn-group btn-group-lg mb-2"
            role="group"
            aria-label="Timer controls"
          >
            <button
              type="button"
              className="btn btn-primary"
              data-testid="play-pause"
              title="Start or pause timer"
              onClick={playPause}
            >
              <span
                className={classNames({
                  oi: true,
                  "oi-media-play": !isTimerRunning,
                  "oi-media-pause": isTimerRunning,
                })}
              />
            </button>
            {/* TODO: Implement stopping the current focus or break session and disable when there is no active session */}
            <button
              type="button"
              className="btn btn-secondary disabled"
              id="stopBtn"
              title="Stop the session"
              onClick={onStop}
            >
              <span className="oi oi-media-stop" />
            </button>
          </div>
        </div>
      </div>
      <div>
        {/* TODO: This area should show only when a focus or break session is running or pauses */}
        <div className="row mb-2 d-none" id="sessionInfo">
          <div className="col">
            {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
            <h2 data-testid="session-title">
              {display}{" "}
              {display === "Focusing for"
                ? minutesToDuration(focusCount)
                : minutesToDuration(breakCount)}{" "}
              minutes
            </h2>
            <div>{/* delete this div when done testing */}</div>
            {/* TODO: Update message below to include time remaining in the current session */}
            <p className="lead" data-testid="session-sub-title">
              {minutesToDuration(timeRemaining)} remaining
            </p>
            <div className="row mb-2 d-none" id="paused">
              <div className="col">
                <h3>PAUSED</h3>
              </div>
            </div>
          </div>
        </div>
        <div className="row mb-2">
          <div className="col">
            <div className="progress d-none" style={{ height: "20px" }}>
              <div
                className="progress-bar"
                role="progressbar"
                aria-valuemin="0"
                aria-valuemax="100"
                aria-valuenow="0" // TODO: Increase aria-valuenow as elapsed time increases
                style={{ width: "0%" }} // TODO: Increase width % as elapsed time increases
              />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Pomodoro;
