import React from "react";

function Paused({ isTimerRunning }) {
  console.log("HERE:", isTimerRunning);
  const paused = (
    <div className="row mb-2 d-none" id="paused">
      <div className="col">
        <h3>PAUSED</h3>
      </div>
    </div>
  );

  if (!isTimerRunning) {
    return paused;
  } else {
    return <p>running</p>;
  }
}
export default Paused;
